chrome.runtime.onInstalled.addListener(()=>{chrome.contextMenus.removeAll(()=>{chrome.contextMenus.create({id:"open-pdf-reader",title:"開啟 PDF 閱讀器",contexts:["all"]})})});chrome.contextMenus.onClicked.addListener((e,t)=>{e.menuItemId==="open-pdf-reader"&&chrome.tabs.create({url:chrome.runtime.getURL("pdf.html")})});chrome.runtime.onMessage.addListener((e,t,r)=>{if(e.type==="OPEN_GRAPH_TAB"&&e.text){const n=chrome.runtime.getURL(`index.html?text=${encodeURIComponent(e.text)}`);chrome.tabs.create({url:n})}});
